INSERT INTO `albuns` (`id`, `titulo`, `slug_titulo`, `descricao`, `imagens`, `data`, `aprovar`, `autor`) VALUES (1,'Bonsai','bonsai','<p>Bonsai significa \"&aacute;rvore em bandeja\". Ele precisa ter outros atributos entretanto al&eacute;m de simplesmente estar num vaso raso (bandeja). A planta deve ser uma r&eacute;plica de uma &aacute;rvore da natureza em miniatura. Deve simular os padr&otilde;es de crescimento e os efeitos da gravidade sobre os galhos, al&eacute;m das marcas do tempo e estrutura geral dos galhos. Essencialmente &eacute; uma obra de arte produzida pelo homem atrav&eacute;s de cuidados especializados.</p>','bonsai_1.jpg, bonsai_10.jpg, bonsai_11.jpg, bonsai_12.jpg, bonsai_13.jpg, bonsai_14.jpg, bonsai_15.jpg, bonsai_16.jpg, bonsai_17.jpg, bonsai_18.jpg, bonsai_19.jpg, bonsai_2.jpg, bonsai_20.jpg, bonsai_21.jpg, bonsai_22.jpg, bonsai_23.jpg, bonsai_24.jpg, bonsai_25.jpg, bonsai_26.jpg, bonsai_27.jpg, bonsai_28.jpg, bonsai_29.jpg, bonsai_3.jpg, bonsai_30.jpg, bonsai_31.jpg, bonsai_32.jpg, bonsai_33.jpg, bonsai_34.jpg, bonsai_35.jpg, bonsai_36.jpg, bonsai_37.jpg, bonsai_38.jpg, bonsai_39.jpg, bonsai_4.jpg, bonsai_40.jpg, bonsai_41.jpg, bonsai_42.jpg, bonsai_43.jpg, bonsai_44.jpg, bonsai_45.jpg, bonsai_46.jpg, bonsai_47.jpg, bonsai_48.jpg, bonsai_49.jpg, bonsai_5.jpg, bonsai_50.jpg, bonsai_51.jpg, bonsai_6.jpg, bonsai_7.jpg, bonsai_8.jpg, bonsai_9.jpg','2015-02-24','Sim',1),(2,'Animes Sexys','animes-sexys','<p>Mang&aacute; se refere aos quadrinhos estilo Japon&ecirc;s e o Anime &eacute; o desenho animado feito com esses quadrinhos. Sele&ccedil;&atilde;o com v&aacute;rios desenhos nestes estilos mostrando muita sensualidade.</p>','animes-sexys_1.jpg, animes-sexys_10.jpg, animes-sexys_11.jpg, animes-sexys_12.jpg, animes-sexys_13.jpg, animes-sexys_14.jpg, animes-sexys_15.jpg, animes-sexys_16.jpg, animes-sexys_17.jpg, animes-sexys_18.jpg, animes-sexys_19.jpg, animes-sexys_2.jpg, animes-sexys_20.jpg, animes-sexys_21.jpg, animes-sexys_22.jpg, animes-sexys_23